// AWS Lambda: remediation trigger (skeleton)
const { SSMClient, SendCommandCommand } = require('@aws-sdk/client-ssm');
const ssm = new SSMClient({ region: process.env.REGION });
exports.handler = async (event) => {
  // derive action from anomaly context
  const params = {
    InstanceIds: ['i-0123456789abcdef0'],
    DocumentName: 'AWS-RunShellScript',
    Parameters: { commands: ['sudo systemctl restart sap'] }
  };
  const cmd = new SendCommandCommand(params);
  await ssm.send(cmd);
  return 'remediation issued';
};
```

