const Dashboard = React.lazy(() => import('./Dashboard'));

// Webpack chunk splitting configuration
optimization: {
  splitChunks: {
    chunks: 'all',
    cacheGroups: {
      vendor: {
        test: /[\\/]node_modules[\\/]/,
        name: 'vendors',
