const {BigQuery} = require('@google-cloud/bigquery');
const bigquery = new BigQuery();

exports.ingestTelemetry = async (req, res) => {
  const msg = JSON.parse(Buffer.from(req.body.message.data, 'base64').toString());

  // Validate schema
  if (!msg.event_id || !msg.timestamp || !msg.payload) {
    return res.status(400).json({error: 'Invalid schema'});
  }

  // Insert to BigQuery with idempotency
  await bigquery.dataset('telemetry').table('events').insert({
    rows: [{json: msg}],
    insertId: msg.event_id
  });

  res.status(200).send();
};
```

