// Example: decrypt with per-tenant CMEK
const {KeyManagementServiceClient} = require('@google-cloud/kms');
const client = new KeyManagementServiceClient();
async function decryptTenantKey(tenantId, ciphertext) {
  const name = `projects/my-project/locations/global/keyRings/tenant-${tenantId}/cryptoKeys/data-key`;
  const [res] = await client.decrypt({ name, ciphertext });
  return res.plaintext;
}
```

